package bd.edu.seu.messmanagmentsystem.controller;import bd.edu.seu.messmanagmentsystem.error_json.ErrorJson;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.boot.web.servlet.error.ErrorAttributes;import org.springframework.boot.web.servlet.error.ErrorController;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.context.request.WebRequest;import javax.servlet.http.HttpServletResponse;import java.util.Map;@RestControllerpublic class DefaultErrorController implements ErrorController {    private static final String PATH="/error";    @Value(value = "${debug}")    private boolean debug;    @Autowired    private ErrorAttributes errorAttributes;    @RequestMapping(value = PATH)    ErrorJson errorJson(HttpServletResponse response,WebRequest webRequest){        return new ErrorJson(response.getStatus(),getErrorAttributes(webRequest,debug));    }    private Map<String, Object> getErrorAttributes(WebRequest webRequest, boolean debug) {        return errorAttributes.getErrorAttributes(webRequest,debug);    }    @Override    public String getErrorPath() {        return PATH;    }}