package bd.edu.seu.messmanagmentsystem.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.sql.DataSource;@RestController@RequestMapping("/api/v1/profile")public class ProfileController {    @Value(value = "${welcome.message}")    private String message;    @Autowired    private DataSource dataSource;    @GetMapping    public String getMessage(){        return message+"/nDatasource : "+dataSource;    }}