package bd.edu.seu.messmanagmentsystem.model;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.Column;import javax.persistence.Embeddable;@Embeddable@Data@AllArgsConstructor@NoArgsConstructorpublic class Name {    @Column(name = "First_Name",length = 50,nullable = false)    private String firstName;    @Column(name = "Middle_Name",length = 50,nullable = false)    private String middleName;    @Column(name = "Last_Name",length = 50,nullable = false)    private String lastName;    @Override    public String toString() {        return firstName+" "+middleName+" "+lastName;    }}