package bd.edu.seu.messmanagmentsystem.repository;import bd.edu.seu.messmanagmentsystem.model.Bazar;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.List;@Repositorypublic interface BazaRepository extends CrudRepository<Bazar, Long>{    @Query(value = "SELECT * from bazar WHERE date BETWEEN ? AND ?",nativeQuery = true)    public List<Bazar> getBazar(LocalDate start,LocalDate end);    @Query(value = "SELECT SUM(cost) from bazar WHERE date BETWEEN ? AND ?",nativeQuery = true)    public double BazarCostForSingleMonth(LocalDate start,LocalDate end);    @Query(value = "SELECT * from bazar WHERE member_member_id=?",nativeQuery = true)    public List<Bazar> getBazarDetailsForSingleMember(long member);}