package bd.edu.seu.messmanagmentsystem.repository;import bd.edu.seu.messmanagmentsystem.model.Login;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Repositorypublic interface LoginRepository extends JpaRepository<Login,String> {    @Query(value = "SELECT user FROM Login user where user.username=?1")    Login getUserNameByName(String name);    @Query(value = "SELECT user_name FROM login_table",nativeQuery = true)    List<String> getAll();    @Transactional    @Modifying    @Query(value = "UPDATE login_table SET pass_word=? WHERE user_name=?",nativeQuery = true)    void updatePass(String pass,String user);}