package bd.edu.seu.messmanagmentsystem.service;import bd.edu.seu.messmanagmentsystem.exceptions.ResourceAlreadyExistsException;import bd.edu.seu.messmanagmentsystem.exceptions.ResourceNotExistException;import bd.edu.seu.messmanagmentsystem.model.UserDeposit;import bd.edu.seu.messmanagmentsystem.repository.UserDepositRepository;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;@Servicepublic class UserDepositService {    private UserDepositRepository userDepositRepository;    public UserDepositService(UserDepositRepository userDepositRepository) {        this.userDepositRepository = userDepositRepository;    }    public List<UserDeposit> getAccountDetails() throws ResourceNotExistException {        List<UserDeposit> userDepositList = new ArrayList<>();        userDepositRepository.findAll().forEach(userDepositList::add);        if(!userDepositList.isEmpty()){            return userDepositList;        }else{            throw new ResourceNotExistException("no user available...");        }    }    public void addDeposite(UserDeposit userDeposit) throws ResourceAlreadyExistsException {        if(!userDepositRepository.existsById(userDeposit.getDepositNo())){            userDepositRepository.save(userDeposit);        }else{            throw new ResourceAlreadyExistsException("this is already present...");        }    }}